    private byte[] getAccessionalData() {
        if (accessionalData == null)
            synchronized (this) {
                if (accessionalData == null)
                    accessionalData = Util.hexToByte(ACCESSIONAL_HEX);
            }
        return accessionalData;
    }

    private boolean checkLibValid() {

        return Sspe.isSoValid();
    }

    public void init(AIFespCarConfig config, AIFespCarListener listener) {
        super.init();
        parseConfig(config);
        if (!checkLibValid() && listener != null) {
            listener.onInit(AIConstant.OPT_FAILED);
            listener.onError(new AIError(AIError.ERR_SO_INVALID, AIError.ERR_DESCRIPTION_SO_INVALID));
            Log.e(TAG, "so动态库加载失败 !");
            return;
        }
        mSpeechListener.setListener(listener);
        if (mFespxProcessor == null) {
            mFespxProcessor = new FespxProcessor();
        }
        mBaseProcessor = mFespxProcessor;
        mBaseProcessor.setCallbackInMainThread(true);
        mFespxProcessor.init(mSpeechListener, signalConfig);
    }

    private void parseConfig(AIFespCarConfig config) {
        if (config == null) {
            Log.e(TAG, "AILocalSignalAndWakeupConfig is null !");
            return;
        }
        super.parseConfig(config, signalConfig);
        Log.d(TAG, "AILocalSignalAndWakeupConfig " + config);
        signalConfig.setImplBfCk(false); // 车载引擎不使用 BeamformingCallback
        signalConfig.setVad(config.isVad()); // 车载的引擎 默认是开启的 以防误唤醒
        signalConfig.setSspeType(config.getSspeType());
        //2.33.2的版本如果是用车载的二麦和四麦， 使用车载的sspe.so
        JNIFlag.isLoadCarSspe = true;

        signalConfig.setThreshs2(config.getLowThreshold());
        signalConfig.setBoundary(config.getBoundary());
        signalConfig.setFespcarDataReceivedEnable(config.getFespCallBackResultData());

        // 适配半字唤醒逻辑
        if (config.getSubWakeupWord() != null) {
            parseWithSubWakeupWord(config);
        } else {
            signalConfig.setThreshs(config.getThreshold());
            signalConfig.setWakupWords(config.getWakeupWordArray());
            signalConfig.setMajors(config.getMajors());
            signalConfig.setDchecks(config.getDcheck());
        }


        switch (config.getSspeType()) {
            case FESP_CAR_MIC_FOUR:
                signalConfig.setMicType(DUILiteConfig.TYPE_COMMON_FESPCAR4);
                break;
            case FESP_CAR_MIC_TWO:
                signalConfig.setMicType(DUILiteConfig.TYPE_COMMON_FESPCAR);
                break;
        }

        //signalConfig.setMaxMessageQueueSize(config.getMaxMessageQueueSize());
        parseConfigWakeupword(config.getWakeupWordArray(), config.getThreshold(), config.getLowThreshold(), config.getMajors(), config.getDcheck());

        // resource in assets
        final List<String> resourceInAssetsList = new ArrayList<>();

        // wakeupResource
        final String wakeupResource = config.getWakeupResource();
        parseWakeupResConfig(resourceInAssetsList, wakeupResource);

        // sspeResource
        final String sspeResource = config.getSspeResource();
        if (TextUtils.isEmpty(sspeResource)) {
            Log.e(TAG, "sspeResource not found !!");
            signalConfig.setSspeBinPath(null);
        } else if (sspeResource.startsWith("/")) {
            signalConfig.setSspeBinPath(sspeResource);
        } else {
            resourceInAssetsList.add(sspeResource);
            signalConfig.setSspeBinPath(Util.getResourceDir(signalConfig.getContext()) + File.separator + sspeResource);
        }

        if (!config.isSspe()) {

            // beamformingResource
            final String beamformingResource = config.getBeamformingResource();
            if (TextUtils.isEmpty(beamformingResource)) {
                Log.e(TAG, "beamformingResource not found !!");
            } else if (beamformingResource.startsWith("/")) {
                signalConfig.setBeamformingBinPath(beamformingResource);
            } else {
                resourceInAssetsList.add(beamformingResource);
                signalConfig.setBeamformingBinPath(Util.getResourceDir(signalConfig.getContext()) + File.separator + beamformingResource);
            }

            // aecResource
            final String aecResource = config.getAecResource();
            if (TextUtils.isEmpty(aecResource)) {
                // 不设置 AEC 资源即不进行 AEC 处理
                Log.w(TAG, "aecResource not found !!");
                Log.w(TAG, "setAecBinPath OFF");
                signalConfig.setAecBinPath("OFF");
            } else if (aecResource.startsWith("/")) {
                signalConfig.setAecBinPath(aecResource);
            } else {
                resourceInAssetsList.add(aecResource);
                signalConfig.setAecBinPath(Util.getResourceDir(signalConfig.getContext()) + File.separator + aecResource);
            }

        }

        signalConfig.setFourHost(config.isFourHost());
        signalConfig.setAssetsResNames(resourceInAssetsList.toArray(new String[resourceInAssetsList.size()]));
        signalConfig.setOneshotConfig(config.getOneshotConfig() == null ? null : parseOneshotConfig(config.getOneshotConfig()));

        signalConfig.setRanges(config.getRanges());
    }

    private void parseWakeupResConfig(List<String> resourceInAssetsList, String wakeupResource) {
        if (TextUtils.isEmpty(wakeupResource)) {
            Log.e(TAG, "wakeupResource not found !!");
            Log.w(TAG, "setWakupBinPath OFF");
            signalConfig.setWakupBinPath("OFF");
        } else if (wakeupResource.startsWith("/")) {
            signalConfig.setWakupBinPath(wakeupResource);
        } else {
            resourceInAssetsList.add(wakeupResource);
            signalConfig.setWakupBinPath(Util.getResourceDir(signalConfig.getContext()) + File.separator + wakeupResource);
        }
    }

    private void parseRangesWords(int[] ranges, String[] wakeupWords) {
        if (ranges != null && wakeupWords != null && ranges.length == wakeupWords.length) {
            List<String> rangesWords = new ArrayList<>();
            for (int i = 0; i < ranges.length; i++) {
                if (ranges[i] == 0) {
                    rangesWords.add(wakeupWords[i]);
                }
            }
            if (mFespxProcessor != null && rangesWords.size() > 0) {
                mFespxProcessor.setRangesWords(rangesWords);
            }
        }
    }

    /**
     * 解析oneshot config 配置
     *
     * @param config {@link AIOneshotConfig}
     * @return {@link  OneshotConfig}
     */
    private OneshotConfig parseOneshotConfig(AIOneshotConfig config) {
        OneshotConfig oneshotConfig = new OneshotConfig();
        LocalVadConfig vadConfig = new LocalVadConfig();

        vadConfig.setPauseTime(0);//oneshot set vad pauseTime 0

        if (!config.getResBin().startsWith("/")) {
            vadConfig.setAssetsResNames(new String[]{config.getResBin()});
            vadConfig.setResBinPath(Util.getResourceDir(AISpeech.getContext()) + File.separator + config.getResBin());
        } else {
            vadConfig.setResBinPath(config.getResBin());
        }

        oneshotConfig.setVadConfig(vadConfig);
        oneshotConfig.setCacheAudioTime(config.getCacheAudioTime());
        oneshotConfig.setMiddleTime(config.getMiddleTime());
        oneshotConfig.setWords(config.getWords());

        return oneshotConfig;
    }

    /**
     * 解析并拼接半字唤醒相关逻辑
     *
     * @param carConfig
     */
    private void parseWithSubWakeupWord(AIFespCarConfig carConfig) {
        AIFespCarConfig.SubWakeupWord subWakeupWord = carConfig.getSubWakeupWord();
        int length = subWakeupWord.getPinyin().length;

        String[] wakupWords = carConfig.getWakeupWordArray();
        float[] threshold = carConfig.getThreshold();
        int[] majors = carConfig.getMajors();
        int[] dchecks = carConfig.getDcheck();


        wakupWords = ArrayUtils.concat(wakupWords, subWakeupWord.getPinyin());
        threshold = ArrayUtils.concat(threshold, subWakeupWord.getThreshold());

        if (majors != null && majors.length > 0) {
            int[] appendMajors = new int[length];
            Arrays.fill(appendMajors, 0);
            majors = ArrayUtils.concat(majors, appendMajors);
        }

        if (dchecks != null && dchecks.length > 0) {
            int[] appendDchecks = new int[length];
            Arrays.fill(appendDchecks, 0);
            dchecks = ArrayUtils.concat(dchecks, appendDchecks);
        }

        signalConfig.setWakupWords(wakupWords);
        signalConfig.setThreshs(threshold);
        signalConfig.setMajors(majors);
        signalConfig.setDchecks(dchecks);

        // 适配公版原有逻辑
        if (wakupWords.length > 0) {
            int[] subWakeupWordArray = new int[wakupWords.length];
            Arrays.fill(subWakeupWordArray, 0);
            // 默认最后一个为半字唤醒
            subWakeupWordArray[subWakeupWordArray.length - 1] = 1;
            signalConfig.setSubwordWakeup(subWakeupWordArray);
        }

    }


    private void parseIntent(AIFespCarIntent aiLocalSignalAndWakeupIntent) {
        if (aiLocalSignalAndWakeupIntent == null) {
            Log.e(TAG, "AILocalSignalAndWakeupIntent is null !");
            return;
        }
        super.parseIntent(aiLocalSignalAndWakeupIntent, mParam);
        Log.d(TAG, "AILocalSignalAndWakeupIntent " + aiLocalSignalAndWakeupIntent);
        mParam.setSaveAudioPath(aiLocalSignalAndWakeupIntent.getSaveAudioFilePath());
        mParam.setUseCustomFeed(aiLocalSignalAndWakeupIntent.isUseCustomFeed());
        mParam.setDumpAudioPath(aiLocalSignalAndWakeupIntent.getDumpWakeupAudioPath());
        mParam.setDumpTime(aiLocalSignalAndWakeupIntent.getDumpWakeupTime());
        mParam.setInputContinuousAudio(aiLocalSignalAndWakeupIntent.isInputContinuousAudio());
        mParam.setAddExtraAudioWhenFeedNotContinuousAudio(aiLocalSignalAndWakeupIntent.isAddExtraAudioWhenFeedNotContinuousAudio());
        mParam.setAutoSetDriveMode(aiLocalSignalAndWakeupIntent.isAutoHoldBeamforming());
        processDoaInformation = aiLocalSignalAndWakeupIntent.isProcessDoaInformation();
    }

    /**
     * 启动信号处理模块和唤醒引擎
     *
     * @param aiFespCarIntent 前端信号处理参数
     * @see AIFespCarIntent
     */
    public void start(AIFespCarIntent aiFespCarIntent) {
        super.start();
        parseIntent(aiFespCarIntent);
        if (mFespxProcessor != null) {
            mFespxProcessor.start(mParam);
        }
    }

