    /**
     * 检查唤醒词是否合法，去除不正确的唤醒词
     *
     * @param words
     * @param thresholds
     * @param majors
     * @param ranges
     * @param isAppend   是否为添加模式 模式为false
     * @return
     */
    private Map checkWakeupInfos(String[] words, float[] thresholds, int[] majors, int[] ranges, boolean isAppend) {
        List<String> wordsArray = new ArrayList<>(Arrays.asList(words));
        int index = 0;
        for (int i = 0, j = wordsArray.size(); i < j; i++) {
            String temp = wordsArray.get(i);
            if (PinYinUtils.isIllegalPinyin(temp)) {
                words = (String[]) ArrayUtils.remove(words, i + index);
                Log.e(TAG, "delete illegal word:" + temp);
                if (mSpeechListener != null) {
                    mSpeechListener.onError(new AIError(AIError.ERR_WAKEUP_NOT_SUPPORT_IN_RES, AIError.ERR_DESCRIPTION_WAKEUP_NOT_SUPPORT_IN_RES + ": " + temp));
                }
                thresholds = (float[]) ArrayUtils.remove(thresholds, i + index);
                majors = (int[]) ArrayUtils.remove(majors, i + index);
                ranges = (int[]) ArrayUtils.remove(ranges, i + index);
                index--;
            }
        }

        Map envMap = new HashMap();

        if (isAppend) {
            if (ArrayUtils.getLength(words) > 0) {
                String[] allWords = null;
                if (words != null) {
                    allWords = ArrayUtils.concat(words, signalConfig.getWakupWords());
                    Log.d(TAG, "allWords " + allWords.length);
                }
                if (allWords != null) {
                    envMap.put(LocalSignalProcessingConfig.KEY_WORD, allWords);
                }
            }

            if (ArrayUtils.getLength(thresholds) > 0) {
                float[] allThresholds = null;
                if (thresholds != null) {
                    allThresholds = ArrayUtils.concat(thresholds, signalConfig.getThreshs());
                }
                if (allThresholds != null) {
                    envMap.put(LocalSignalProcessingConfig.KEY_THRESH, allThresholds);
                }

            }

            if (ArrayUtils.getLength(words) > 0) {
                int wordsLength = ArrayUtils.getLength(words);
                String[] allSubwords = new String[wordsLength + signalConfig.getWakupWords().length];
                if (words != null) {
                    Arrays.fill(allSubwords, "0");
                    allSubwords[allSubwords.length - 1] = "1";// 默认最后一个是半字唤醒
                }
                envMap.put(LocalSignalProcessingConfig.KEY_SUB_WORD_WAKEUP, allSubwords);
            }

            if (ArrayUtils.getLength(majors) > 0) {
                int[] allMajors = null;
                if (words != null) {
                    allMajors = ArrayUtils.concat(majors, signalConfig.getMajors());
                }
                if (allMajors != null) {
                    envMap.put(LocalSignalProcessingConfig.KEY_MAJOR, allMajors);
                }

            }

            if (ArrayUtils.getLength(ranges) > 0) {
                int[] allRanges = null;
                if (words != null) {
                    allRanges = ArrayUtils.concat(ranges, signalConfig.getRanges());
                }
                if (allRanges != null) {
                    envMap.put(LocalSignalProcessingConfig.KEY_RANGES, allRanges);
                }

            }
        } else {
            if (ArrayUtils.getLength(thresholds) > 0) {
                envMap.put(LocalSignalProcessingConfig.KEY_WORD, words);
            }
            if (ArrayUtils.getLength(thresholds) > 0) {
                envMap.put(LocalSignalProcessingConfig.KEY_THRESH, thresholds);
            }
            if (ArrayUtils.getLength(majors) > 0) {
                envMap.put(LocalSignalProcessingConfig.KEY_MAJOR, majors);
            }
            if (ArrayUtils.getLength(ranges) > 0) {
                envMap.put(LocalSignalProcessingConfig.KEY_RANGES, ranges);
            }
        }

        return envMap;
    }

    private Map checkWakeupInfos(String[] words, float[] thresholds, int[] majors, int[] ranges) {
        return checkWakeupInfos(words, thresholds, majors, ranges, false);
    }

    /**
     * 动态调整参数，可以用来设置唤醒词，阈值等
     * <pre>{@code
     *  Map envMap = new HashMap();
     *  envMap.put("words", new String[]{"ni hao xiao le"});
     *  envMap.put("thresh", new float[]{0.45f});
     *  mEngine.setDynamicParamEnv(envMap);
     * }</pre>
     *
     * @param dynamicParam env 里的参数，key可以是 words thresh thresh2 major等, value 类型可以是 基础类型、字符串、数组、列表
     * @since 2.11.1
     */
    private void setDynamicParamEnv(Map<String, ?> dynamicParam) {
        if (dynamicParam == null || dynamicParam.isEmpty()) {
            Log.i(TAG, "setDynamicParamEnv: dynamicParam isEmpty ");
            return;
        }
        Iterator<String> iter = dynamicParam.keySet().iterator();
        StringBuilder envSb = new StringBuilder();
        while (iter.hasNext()) {
            String key = iter.next();
            Object obj = dynamicParam.get(key);
            String value;
            if (obj == null) {
                continue;
            } else if (obj instanceof Iterable) {
                StringBuilder sb = new StringBuilder();
                Iterator<Object> iterator = ((Iterable) obj).iterator();
                while (iterator.hasNext()) {
                    Object o = iterator.next();
                    sb.append(o);
                    if (iterator.hasNext())
                        sb.append(",");
                }
                value = sb.toString();
            } else if (obj.getClass().isArray()) {
                value = transferTypeArray(obj);
            } else {
                value = String.valueOf(obj);
            }
            envSb.append(key).append("=").append(value).append(";");
        }
        if (boundary != -1) {
            envSb.append("boundary=").append(boundary >= 1 ? "1" : "0").append(";");
        }
        Map<String, Object> envMap = new HashMap<>();
        // 动态设置唤醒env
        envMap.put("env", envSb.toString());
        setDynamicParam(envMap);
    }

    private String transferTypeArray(Object obj) {
        String value;
        StringBuilder sb = new StringBuilder();
        int length = Array.getLength(obj);
        for (int i = 0; i < length; i++) {
            sb.append(Array.get(obj, i));
            if (i < length - 1)
                sb.append(",");
        }
        value = sb.toString();
        return value;
    }

    /**
     * 动态设置唤醒参数，可以在引擎初始化成功后动态设置
     * <pre>{@code
     *  Map<String, Object> dynamicParam = new HashMap<>();
     *  dynamicParam.put("env", "words=ni hao xiao le;thresh=0.45;major=1;");
     *  // maxVolumeState 用于设置大音量状态，需配置AEC资源。启用大音量检测功能时，在每次 feed 之前调用，0 表示非大音量，1 表示大音量
     *  dynamicParam.put("maxVolumeState", 1);
     *  //动态设置开关，1为开，0为关
     *  dynamicParam.put("wakeupSwitch", 0);
     *  dynamicParam.put("doa", 90);
     *  mEngine.setDynamicParam(dynamicParam);
     * }</pre>
     * 此方法可用于设置唤醒词和唤醒词阈值以外，还可以设置其他属性，如唤醒角度，环形开关，大音量等属性
     *
     * @param dynamicParam 动态参数, Map 类型，key 为 String 类型，可以是env ,maxVolumeState,wakeupSwitch,doa   value 类型可以是 基础类型、字符串、数组、列表
     */
    private void setDynamicParam(Map<String, ?> dynamicParam) {
        if (dynamicParam == null || dynamicParam.isEmpty()) {
            Log.i(TAG, "setDynamicParam: dynamicParam isEmpty ");
            return;
        }
        parseDynamicParamEnv(dynamicParam);

        JSONObject setJson = new JSONObject();
        Iterator<String> iter = dynamicParam.keySet().iterator();
        while (iter.hasNext()) {
            String key = iter.next();
            Object value = dynamicParam.get(key);
            try {
                if (value != null) {
                    if (value instanceof Iterable) {
                        transferTypeItera(setJson, key, (Iterable) value);
                    } else if (value.getClass().isArray()) {
                        tranferTypeArray(setJson, key, value);
                    } else {
                        setJson.put(key, value);
                    }
                }
            } catch (JSONException e) {
                e.printStackTrace();
                Log.e(TAG, "setDynamicParam key：" + key + " " + e);
            }
        }

        final String setString = setJson.toString();
        Log.d(TAG, "setString" + setString + ", wakeup env = " + wakupSetEnv);
        if (TextUtils.equals(setString, wakupSetEnv)) {
            Log.e(TAG, "Repetive wake vocabulary ");
        } else {
            wakupSetEnv = setString;
            if (mFespxProcessor != null) {
                Log.d(TAG, "setDynamicParam: " + setString);
                mFespxProcessor.set(setString);
            }
        }
    }

    private void transferTypeItera(JSONObject setJson, String key, Iterable<Object> value) throws JSONException {
        JSONArray jsonArray = new JSONArray();
        for (Object o : value) {
            jsonArray.put(o);
        }
        setJson.put(key, jsonArray);
    }

    private void tranferTypeArray(JSONObject setJson, String key, Object value) throws JSONException {
        JSONArray jsonArray = new JSONArray();
        int length = Array.getLength(value);
        for (int i = 0; i < length; i++) {
            jsonArray.put(Array.get(value, i));
        }
        setJson.put(key, jsonArray);
    }

    private void setDynamicParam(String json) {
        if (mFespxProcessor != null) {
            Log.i(TAG, "setDynamicParam: " + json);
            mFespxProcessor.set(json);
        }
    }

